mapper.select:

	SELECT SEQ, ID, TITLE, CONTENT, REFER, STEP, "DEPTH", READCOUNT, REGDATE, DELFLAG 
	FROM ANSWERBOARD2
	WHERE SEQ = #{seq}

mapper.update:

	UPDATE ANSWERBOARD2 
	SET TITLE =#{title}, CONTENT=#{content}  
	WHERE SEQ=#{seq}


	UPDATE ANSWERBOARD2 SET DELFLAG = 'Y' 
	WHERE DELFLAG = 'N' AND SEQ IN
	
		#{seq}

mapper.delete:
DELETE FROM ANSWERBOARD2 
WHERE SEQ IN
	
			#{seq}
